package com.test.leetcode.erery;

import org.junit.Test;

import java.util.Arrays;
import java.util.HashSet;
import java.util.Set;

/**
 * @ClassName: Solution2039
 * @Description:
 * @Author: qinfajia
 * @Date: 2023/1/27 21:54
 * @Version: 1.0
 */
public class Solution2039 {

    @Test
    public void test() {
        System.out.println(greatestLetter(""));
    }

    public String greatestLetter(String s) {
        Set<Character> set = new HashSet<>();
        for (char c : s.toCharArray()) {
            set.add(c);
        }
        char upper = 'Z', lower = 'z';
        for (int i = 0; i < 26; i++) {
            if (set.contains((char) (upper - i)) && set.contains((char) (lower - i))) {
                return (char) (upper - i) + "";
            }
        }
        return "";
    }
}
